createForm = function (option) {
  var str = `
	<div class="section">
      <div class="row">
        <div class="col s12 c-head-title">
          <div class="c-header">
            <div ${ 'titleID' in option ? ' id="' + option.titleID + '"' : ''} class="c-title ${ 'titleClass' in option ? option.titleClass : ''}">${option.title}</div>
          </div>
        </div>
      </div>
      <div class="card">
        <div class="card-content">
          <div class="form_data row container mt-2">
            <div class="row">
              <form class="col s12">
                ${ option.arrayForm.map( ( forms ) => {
                		return forms;
                	}) }
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>`;
  return str;
}

R.apost = async function (url, token, obj) {
  var options = {
    method: 'post',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json; charset=utf-8'
    },
    body: JSON.stringify(obj)
  }
  if (token != '') options.headers['Authorization'] = 'Bearer ' + token;
  var res = await fetch(url, options)
  if (res.status == 200) return res.json()
  else return {
    'status': res.status
  }
}

